import React, { useState } from 'react';
import '../assets/Login.css';

// Firebase Realtime Database imports
import { db, databaseURL } from '../config/firebase';
import { ref, query, orderByChild, equalTo, get } from 'firebase/database';

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('perawat');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [debugLines, setDebugLines] = useState([]);

  const pushDebug = (msg) => {
    try {
      setDebugLines((d) => [...d.slice(-20), `${new Date().toLocaleTimeString()}: ${String(msg)}`]);
    } catch (e) {
      // ignore
    }
  };

  const handleSubmit = async (e) => {
    if (e && e.preventDefault) e.preventDefault();
  setError('');
  setLoading(true);
  console.log('Login attempt for', username);
  pushDebug('Login attempt for ' + username);

    const name = (username || '').trim();
    if (!name || !password) {
      setLoading(false);
      return setError('Username dan password wajib diisi!');
    }

    // Quick network/DNS check to surface clear error if the RTDB hostname cannot be resolved
    if (databaseURL) {
      const pingUrl = `${databaseURL.replace(/\/$/, '')}/.json`;
      pushDebug('Pinging RTDB URL: ' + pingUrl);
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 5000);
      try {
        const resp = await fetch(pingUrl, { method: 'GET', signal: controller.signal });
        clearTimeout(timeoutId);
        pushDebug('Ping response status: ' + resp.status);
        // If DNS is resolved but access is denied, we can still proceed to let the SDK return a clearer error
      } catch (err) {
        clearTimeout(timeoutId);
        console.error('Network/DNS error when pinging RTDB:', err);
        pushDebug('Network error when contacting RTDB: ' + (err && err.message ? err.message : String(err)));
        // Development fallback: allow local in-memory authentication so developer can continue
        const allowDevFallback = (new URLSearchParams(window.location.search).get('dev') === '1') || process.env.NODE_ENV === 'development';
        if (allowDevFallback) {
          pushDebug('Using dev fallback auth (RTDB unreachable)');
          // simple in-memory users for dev
          const devUsers = {
            'Sergio Poli': { password: '12345', role: 'perawat', uid: 'dev-sergio' },
            'dev': { password: 'devpass', role: 'perawat', uid: 'dev-user' }
          };
          const du = devUsers[name];
          if (du && String(du.password) === String(password)) {
            onLogin({ username: name, role: du.role, uid: du.uid });
            setLoading(false);
            return;
          }

          setError('Gagal menghubungi DB dan kredensial dev tidak cocok.');
          setLoading(false);
          return;
        }

        setError('Gagal menghubungi Realtime Database. Periksa koneksi internet, DNS, atau `databaseURL` di konfigurasi.');
        setLoading(false);
        return;
      }
    }

    try {
      // Query users by child 'nama' equals input username
      const usersRef = ref(db, 'users');
      const q = query(usersRef, orderByChild('nama'), equalTo(name));
        console.log('Running RTDB query for name=', name);
        pushDebug('Running RTDB query for name=' + name);
        const snap = await get(q);
        console.log('RTDB query snapshot:', snap);
        pushDebug('RTDB query returned. exists=' + snap.exists());

      if (!snap.exists()) {
        console.warn('No matching user found for', name);
        setError('User tidak ditemukan.');
        setLoading(false);
        return;
      }

      const matches = snap.val(); // object keyed by uid
  console.log('Query matches:', matches);
  pushDebug('Query matches keys=' + Object.keys(matches).join(','));
      const firstKey = Object.keys(matches)[0];
      const userRecord = matches[firstKey];
  console.log('Using userRecord:', firstKey, userRecord);

      if (!userRecord.password) {
        setError('Akun tidak memiliki password (cek data).');
        setLoading(false);
        return;
      }

      if (String(userRecord.password) === String(password)) {
        const displayName = userRecord.nama || name;
        const userRole = userRecord.role ? userRecord.role.toLowerCase() : role;
        console.log('Authenticated locally; about to call onLogin with', { displayName, userRole, uid: firstKey });
        pushDebug('Authenticated locally for ' + displayName + ' uid=' + firstKey);
        try {
          onLogin({ username: displayName, role: userRole, uid: firstKey });
          console.log('onLogin handler called successfully');
          pushDebug('onLogin handler called');
          setLoading(false);
          // show a small success message before the component unmounts
          setError('');
          return; // stop further execution
        } catch (handlerErr) {
          console.error('onLogin handler threw an error:', handlerErr);
          setError('Gagal memproses login (internal).');
          setLoading(false);
          return;
        }
      } else {
        console.warn('Password mismatch for user', firstKey);
        setError('Password salah.');
        setLoading(false);
        return;
      }
    } catch (err) {
      console.error('RTDB query error:', err);
      setError('Terjadi error saat verifikasi. Cek koneksi.');
      setLoading(false);
      return;
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="medical-icon">
            <svg width="50" height="50" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M12 2L13.09 8.26L20 9L13.09 9.74L12 16L10.91 9.74L4 9L10.91 8.26L12 2Z" fill="#00B4D8"/>
              <circle cx="12" cy="12" r="10" stroke="#0077B6" strokeWidth="2" fill="none"/>
              <path d="M12 8V16M8 12H16" stroke="white" strokeWidth="2" strokeLinecap="round"/>
            </svg>
          </div>
          <h1>Sistem Asuhan Keperawatan</h1>
          <p>Platform Digital untuk Profesional Kesehatan</p>
        </div>
        
        <form className="login-form" onSubmit={handleSubmit}>
          <h2>Masuk ke Akun Anda</h2>
          {error && <div className="error">{error}</div>}
          
          <div className="input-group">
            <label htmlFor="role">Peran</label>
            <select id="role" value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="perawat">üë©‚Äç‚öïÔ∏è Perawat</option>
              <option value="kepala">üë®‚Äçüíº Kepala Rumah Sakit</option>
            </select>
          </div>
          
          <div className="input-group">
            <label htmlFor="username">Username</label>
            <input
              id="username"
              type="text"
              placeholder="Masukkan username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              placeholder="Masukkan password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          
          <button
            type="submit"
            className="login-btn"
            onClick={handleSubmit}
            disabled={loading}
            aria-busy={loading}
          >
            {loading ? (
              <>
                <span>Memeriksa...</span>
                <svg width="20" height="20" viewBox="0 0 50 50" className="spinner" aria-hidden>
                  <circle cx="25" cy="25" r="20" stroke="currentColor" strokeWidth="4" fill="none" strokeDasharray="31.4 31.4" />
                </svg>
              </>
            ) : (
              <>
                <span>Masuk</span>
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                  <path d="M5 12H19M19 12L12 5M19 12L12 19" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </>
            )}
          </button>
          <div className="login-status" aria-live="polite" style={{marginTop: '10px', minHeight: '20px'}}>
            {loading ? 'Memeriksa kredensial...' : ''}
          </div>
          {debugLines && debugLines.length > 0 && (
            <div className="debug-log" style={{marginTop: '12px', maxHeight: '120px', overflow: 'auto', fontSize: '12px', color: '#444'}}>
              {debugLines.map((d, i) => (
                <div key={i} style={{opacity: 0.9}}>{d}</div>
              ))}
            </div>
          )}
        </form>
        
        <div className="login-footer">
          <p>¬© 2025 Sistem Askep Digital</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
