import React, { useState } from 'react';
import '../assets/Login.css';
import { db } from '../config/firebase';
import { ref, query, orderByChild, equalTo, get } from 'firebase/database';

const Login = ({ onLogin }) => {
  const [role, setRole] = useState('perawat');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    if (e && e.preventDefault) e.preventDefault();
    setError('');
    setLoading(true);

    const name = (username || '').trim();
    if (!name || !password) {
      setError('Username dan password wajib diisi!');
      setLoading(false);
      return;
    }

    try {
      // ambil dari firebase berdasarkan nama
      const q = query(ref(db, 'users'), orderByChild('nama'), equalTo(name));
      const snap = await get(q);

      if (!snap.exists()) {
        setError('User tidak ditemukan.');
        setLoading(false);
        return;
      }

      let userRecord;
      let uid;
      snap.forEach((child) => {
        uid = child.key;
        userRecord = child.val();
      });

      // cek password
      if (!userRecord.password) {
        setError('Akun tidak memiliki password.');
        setLoading(false);
        return;
      }

      if (String(userRecord.password).trim() === String(password).trim()) {
        // set user ke app.js ‚Üí otomatis pindah ke dashboard
        const displayName = userRecord.nama || name;
        const userRole = userRecord.role ? userRecord.role.toLowerCase() : role;
        onLogin({ username: displayName, role: userRole, uid });
        setLoading(false);
        return;
      }

      setError('Password salah.');
      setLoading(false);

    } catch (err) {
      console.error('Login RTDB error:', err);
      setError('Terjadi error saat verifikasi. Cek koneksi.');
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="medical-icon">
            <svg width="50" height="50" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M12 2L13.09 8.26L20 9L13.09 9.74L12 16L10.91 9.74L4 9L10.91 8.26L12 2Z" fill="#00B4D8"/>
              <circle cx="12" cy="12" r="10" stroke="#0077B6" strokeWidth="2" fill="none"/>
              <path d="M12 8V16M8 12H16" stroke="white" strokeWidth="2" strokeLinecap="round"/>
            </svg>
          </div>
          <h1>Sistem Asuhan Keperawatan</h1>
          <p>Platform Digital untuk Profesional Kesehatan</p>
        </div>

        <form className="login-form" onSubmit={handleSubmit}>
          <h2>Masuk ke Akun Anda</h2>
          {error && <div className="error">{error}</div>}

          <div className="input-group">
            <label htmlFor="role">Peran</label>
            <select id="role" value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="perawat">üë©‚Äç‚öïÔ∏è Perawat</option>
              <option value="kepala">üë®‚Äçüíº Kepala Rumah Sakit</option>
            </select>
          </div>

          <div className="input-group">
            <label htmlFor="username">Username</label>
            <input
              id="username"
              type="text"
              placeholder="Masukkan username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>

          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              placeholder="Masukkan password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <button type="submit" className="login-btn" disabled={loading} aria-busy={loading}>
            {loading ? 'Memeriksa...' : 'Masuk'}
          </button>
        </form>

        <div className="login-footer">
          <p>¬© 2025 Sistem Askep Digital</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
